script:
frappe.ui.form.on('Tax Withholding Category', {
    refresh: function(frm) {
        // Your script logic here
    },
    onload: function(frm) {
        // Check if the document is in Draft state (new form)
        if (frm.doc.docstatus === 0) {
            // Set a default value for the __newname field during onload for new forms
            frm.set_value('__newname', frm.doc.custom_type + '-' + frm.doc.custom_sub_category);
        }
    },
    custom_section: function(frm) {
        if (frm.doc.category_name) {
            // Update the __newname field when custom_section changes and category_name is not null
            frm.set_value('__newname', frm.doc.custom_type + '-' + frm.doc.custom_section + '-' + frm.doc.category_name + '-' + frm.doc.custom_sub_category);
        } else {
            // Handle the case when category_name is null
            // You can run another query or set a different value as needed
            frm.set_value('__newname', frm.doc.custom_type + '-' + frm.doc.custom_section + '-' + frm.doc.custom_sub_category);
        }

    },
    custom_sub_category: function(frm) {
        // Set a default value for the __newname field by concatenating custom_type and custom_section
        frm.set_value('__newname', frm.doc.custom_type + '-' + frm.doc.custom_section + '-' + frm.doc.category_name + '-' + frm.doc.custom_sub_category);
    },
    custom_type: function(frm) {
        // Set a default value for the __newname field by concatenating custom_type and custom_section
        frm.set_value('__newname', frm.doc.custom_type + '-' + frm.doc.custom_section + '-' + frm.doc.category_name + '-' + frm.doc.custom_sub_category);
    }
});
