var isChequeBookOver = false;

frappe.ui.form.on('Payment Entry', {
    onload: function(frm) {
        setReferenceNoBasedOnAccountTypes(frm);
    },
    paid_from: function(frm) {
        setReferenceNoBasedOnAccountTypes(frm);
    },
    paid_to: function(frm) {
        setReferenceNoBasedOnAccountTypes(frm);
    },
    before_save: function(frm) {
        if (isChequeBookOver) {
            frappe.msgprint(__('Cheque Book is over. Cannot create a new Payment Entry.'));
            frappe.validated = false; // Prevent form save
            return;
        }

        // Check for duplicate reference_no within the document
        var referenceNo = frm.doc.reference_no;

        if (referenceNo && frm.doc.__unsaved && frm.doc.__unsaved.reference_no === referenceNo) {
            frappe.msgprint(__('Duplicate reference number. Please set another number.'));
            frappe.validated = false; // Prevent form save
            return;
        }

        // Check for duplicate reference_no in existing Payment Entry records
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Payment Entry',
                filters: { reference_no: referenceNo },
                limit: 1
            },
            callback: function(response) {
                if (response.message && response.message.length > 0) {
                    frappe.msgprint(__('Duplicate reference number. Please set another number.'));
                    frappe.validated = false; 
                    fetchChequeBookInfoAndSetReferenceNo(frm);
                }
            }
        });
    }
});

function setReferenceNoBasedOnAccountTypes(frm) {
    var paidFromAccount = frm.doc.paid_from;
    var paidToAccount = frm.doc.paid_to;

    // Fetch the account type for paid_from
    fetchAccountType(paidFromAccount, function(accountTypeFrom) {
        console.log('Paid From Account Type:', accountTypeFrom);

        // Fetch the account type for paid_to
        fetchAccountType(paidToAccount, function(accountTypeTo) {
            console.log('Paid To Account Type:', accountTypeTo);

            if (accountTypeFrom === 'Bank' || accountTypeTo === 'Bank') {
                if (frm.is_new()) {
                    fetchChequeBookInfoAndSetReferenceNo(frm);
                }
            } else {
                // Set reference_no to a different value if needed
                frm.set_value('reference_no', '');
            }
        });
    });
}

function fetchAccountType(account, callback) {
    frappe.call({
        method: 'frappe.client.get_value',
        args: {
            doctype: 'Account',
            fieldname: 'account_type',
            filters: { name: account }
        },
        callback: function(response) {
            var accountType = response.message ? response.message.account_type : '';
            callback(accountType);
        }
    });
}
function fetchChequeBookInfoAndSetReferenceNo(frm) {
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Cheque Book',
            fields: ['name', 'from_no', 'last_no'],
            filters: {},
            order_by: 'creation desc',
            limit: 1
        },
        callback: function(response) {
            if (response.message && response.message.length > 0) {
                var chequeBooksList = response.message;
                var fromNo = chequeBooksList[0].from_no;
                var lastNo = chequeBooksList[0].last_no;

                frappe.call({
                    method: 'frappe.client.get_list',
                    args: {
                        doctype: 'Payment Entry',
                        fields: ['reference_no'],
                        order_by: 'reference_no desc', // Descending order to find the highest existing reference_no
                        limit: 1
                    },
                    callback: function(lastResponse) {
                        var existingReferenceNos = lastResponse.message.map(entry => parseInt(entry.reference_no));
                        var nextReferenceNo = findNextReferenceNoInRange(existingReferenceNos, fromNo, lastNo);

                     if (nextReferenceNo !== null && nextReferenceNo <= lastNo) {
                            // There is a gap in the sequence
                            frappe.msgprint(__('Cheque Book is over. Cannot create a new Payment Entry.'));
                            frm.set_value('reference_no', '');
                        } else {
                            frm.set_value('reference_no', nextReferenceNo);
                        }
                    }
                });
            }
        }
    });
}

function findNextReferenceNoInRange(existingReferenceNos, fromNo, lastNo) {
    // Convert existing reference numbers to integers
    existingReferenceNos = existingReferenceNos.map(Number);

    // Generate an array of numbers within the specified range
    var range = Array.from({ length: lastNo - fromNo + 1 }, (_, index) => fromNo + index);

    // Filter out existing reference numbers from the generated range
    var availableNumbers = range.filter(number => !existingReferenceNos.includes(number));

    // If there are available numbers, return the highest one; otherwise, return null
    return availableNumbers.length > 0 ? Math.max(...availableNumbers) : null;
}

